<template>
  <div id="app">
    <router-view></router-view>
    <div>
      <tabbar>
        <tabbar-item>
          <div slot="icon">
            <img src="data:image/png;base64," alt="">
          </div>
          <span slot="label">Contacts</span>
        </tabbar-item>
        <tabbar-item>
          <div slot="icon">
            <img src="data:image/png;base64," >
          </div>
          <span slot="label">Chats</span>
        </tabbar-item>
        <tabbar-item>
          <div slot="icon">
            <img src="data:image/png;base64," alt="">
          </div>
          <span slot="label">Settings</span>
        </tabbar-item>
      </tabbar>
    </div>
  </div>
</template>

<script>
import { router } from './main'
import Tabbar from 'vux-components/tabbar/tabbar'
import TabbarItem from 'vux-components/tabbar/tabbar-item'

export default {
  components: {
    Tabbar,
    TabbarItem
  },
  events: {
    'on-item-click': function (index) {
      switch (index) {
        case '0': router.go({name: 'contacts'})
          break
        case '1': router.go({name: 'chats'})
          break
        case '2': router.go({name: 'settings'})
          break
      }
    }
  }
}
</script>

